package com.demo.datatypes;

public class DataTypesDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte myByte = 1;
		short noOfSubjects = 31;
		int rollNumber = 10;
		long jobId = 183943789;
		float marks = 10.00f;
		double amount = 200.00;
		boolean isTrue = true;
		char letter = 'a';
		
		System.out.println("Byte variable: "+myByte+"\nNumber Of Subjects: "+noOfSubjects
			+"\nRoll No: "+rollNumber+"\nJob Id: "+jobId
			+"\nTotal Marks: "+marks+"\nAmount: "+amount
			+"\nIs True?: "+isTrue+"\nInitial Letter: "+letter);
	}

}
